// JS App Files /=============================================================
var EventLog = {
  eventbindings: [],  // All the subscriptions
  last_ts: Mailpile.local_storage['eventlog_last_ts'] || -1800,
  first_load: true,
  timer: null
};


EventLog.pause = function() {
  return EventLog.timer.pause();
};


EventLog.play = function() {
  return EventLog.timer.play();
};


EventLog.request = function(conditions, callback) {
  if (EventLog.first_load) {
    Mailpile.API.eventlog_get({incomplete: true}, EventLog.invoke_callbacks);
    EventLog.first_load = false;
  }
  var conditions = conditions || {};
  conditions._error_callback = EventLog.process_error;
  Mailpile.API.eventlog_get(conditions, callback || EventLog.process_result);
};


EventLog.poll = function() {
  // BRE: disabled the filtering for now, as the eventlog filter language
  //      is not flexible enough to watch for all the different events
  //      we need in one call. It also has the issue that if subscriptions
  //      change then we need the ability to immediately terminate the
  //      outstanding request and fire off a new one. So for now we just
  //      use the firehose, but increase the gather time to 1 second.
  //
  // Request news about updates to the mail sources and command cache
  //var source_re = '~(.mail_source|.command_cache';
  //
  // ... and any other things we consider exciting
  //for (id in EventLog.eventbindings) {
  //   binding = EventLog.eventbindings[id][0];
  //   source_re += '|' + binding.source;
  //}
  //source_re += ')';

  EventLog.request({
  //source: source_re,
    since: EventLog.last_ts,
    gather: (EventLog.last_ts < 0) ? 0.2 : 1.0,
    wait: 30,
    _timeout: 31000
  });
};


EventLog.invoke_callbacks = function(response) {
  // Update the API CSRF token
  Mailpile.csrf_token = response.state.csrf_token;

  // Iterate through the events, calling callbacks...
  var last_ts = response.result.ts;
  for (event in response.result.events) {
    var ev = response.result.events[event];
    for (id in EventLog.eventbindings) {
      binding = EventLog.eventbindings[id][0];
      callback = EventLog.eventbindings[id][1];
      if ((!binding.source || ev.source.match(new RegExp(binding.source)))
          && (!binding.event_id || ev.event_id == binding.event_id)
          && (!binding.flags || ev.flags.match(new RegExp(binding.flags)))) {
        callback(ev);
      }
    }
    last_ts = response.result.ts;
  }
  return last_ts;
};


EventLog.process_error = function(result, textstatus) {
  setTimeout(function() {EventLog.poll();}, 5000);
};


EventLog.process_result = function(result, textstatus) {
  EventLog.last_ts = EventLog.invoke_callbacks(result);
  EventLog.poll();
  Mailpile.local_storage['eventlog_last_ts'] = EventLog.last_ts;
};


EventLog.subscribe = function(ev, func) {
  // Subscribe a function to an event.
  // Returns a subscription ID.
  if (!$.isFunction(func)) {
    console.log("Can only subscribe functions");
    return false;
  }
  if (typeof(ev) == "string") {
    ev = {source: ev, event_id: null};
  }
  this.eventbindings.push([ev, func]);
  return this.eventbindings.length - 1;
};


EventLog.unsubscribe = function(ev, func_or_id) {
  // Given an event class and a subscription id
  // or a function, will unsubscribe from the event.
  // Returns true if successfully unsubscribed.
  if ($.isFunction(func_or_id)) {
    for (i in this.eventbindings) {
      if (this.eventbindings[i][1] == func_or_id) {
        this.eventbindings.splice(i, 1);
        return true;
      }
    }
  } else {
    this.eventbindings.splice(func_or_id, 1);
    return true;
  }
  return false;
};/* Activities */
Mailpile.activities.compose = function(to, from) {
  var compose_data = {};
  if (to) compose_data.to = to;
  if (from) compose_data.from = from;
	Mailpile.API.message_compose_post(compose_data, function(response) {
    if (response.status === 'success') {
      Mailpile.go(Mailpile.urls.message_draft + response.result.message_ids[0] + '/');
    } else {
      Mailpile.notification(response);
    }
  });
};


Mailpile.activities.render_typeahead = function() {

  var baseMatcher = function(strs) {
    return function findMatches(q, cb) {
      var matches, substrRegex;
      matches = [];
      substrRegex = new RegExp(q, 'i');
      $.each(strs, function(i, str) {
        if (substrRegex.test(str.term)) {
          matches.push(str);
        }
      });
      cb(matches);
    };
  };

  var tagMatcher = function(strs) {
    return function findMatches(q, cb) {
      var matches, substrRegex;
      matches = [];
      substrRegex = new RegExp(q, 'i');
      $.each(strs, function(i, str) {
        if (substrRegex.test(str.name)) {
          matches.push(str);
        }
      });
      cb(matches);
    };
  };

  var peopleMatcher = function(strs) {
    return function findMatches(q, cb) {
      var matches, substrRegex;
      matches = [];
      substrRegex = new RegExp(q, 'i');
      $.each(strs, function(i, str) {
        if (substrRegex.test(str.fn) || substrRegex.test(str.address)) {
          str.term =  'from:';
          matches.push(str);
        }
      });
      cb(matches);
    };
  };

  // List of basic suggestions for search helpers
  var helpers = [
    { term: 'dates:', helper: '2011-12..2012-04-15' },
    { term: 'date:', helper: 'date:2013-8-3 +date:2013-9-10' },
    { term: 'year:', helper: '2013' },
    { term: 'month:', helper: '8' },
    { term: 'subject:', helper: 'any normal words' },
    { term: 'att:', helper: 'jpg' },
    { term: 'has:', helper: 'attachment' },
    { term: 'contacts: ', helper: 'name@email.com' },
    { term: 'to:', helper: 'name@email.com' }
  ];

  // Create Typeahead
  $('#form-search .typeahead').typeahead({
    hint: true,
    highlight: true,
    minLength: 0
  },{
    name: 'search',
    displayKey: 'term',
    source: baseMatcher(helpers),
    templates: {
      suggestion: function(data) {
        var template = Mailpile.safe_template('<div class="tt-suggestion"><p><span class="icon-search"></span> <%= term %> <span class="helper"><%= helper %></span></p></div>');
        return template(data);
      }
    }
  },{
    name: 'tags',
    displayKey: function(value) {
      return 'in:' + value.slug
    },
    source: tagMatcher(Mailpile.instance.tags),
    templates: {
      empty: '<div class="tt-suggestion"><p><span class="icon-tag"></span> No tags match your search</p></div>',
      suggestion: function(data) {
        if (data.display !== 'invisible') {
          var template = Mailile.safe_template('<div class="tt-suggestion"><p><span class="color-<%= label_color %> <%= icon %>"></span> <%= name %></p></div>');
          return template(data);
        }
      }
    }
  },{
    name: 'people',
    displayKey: function(value) {
      return value.term + value.address;
    },
    source: peopleMatcher(Mailpile.instance.addresses),
    templates: {
      header: '<span class="separator"></span>',
      empty: '<div class="tt-suggestion"><p><span class="icon-user"></span> No people match your search</p></div>',
      suggestion: function(data) {
        if (data.photo === undefined) { data.photo = '/static/img/avatar-default.png'; }
        var template = Mailpile.safe_template('<div class="tt-suggestion"><p><img class="avatar" src="<%= photo %>"> <%= term %> <%= fn %></p></div>');
        return template(data);
      }
    }
  },{
    name: 'keys',
    displayKey: 'term',
    source: baseMatcher([{ term: 'keys: team@mailpile.is' },{ term: 'keys: 707775F9' }]),
    templates: {
      suggestion: function(data) {
        var template = Mailpile.safe_template('<div class="tt-suggestion"><p><span class="icon-key"></span> <%= term %></p></div>');
        return template(data);
      }
    }
  });

};Mailpile.fix_url = function(url) {
  if (url.indexOf("") != 0) {
    return "" + url;
  }
  return url;
}

Mailpile.go = function(url) {
  Mailpile.notify_working(undefined, 1000);
  window.location.href = Mailpile.fix_url(url);
};


Mailpile.bulk_cache_human_length = function(type) {
  if (_.indexOf(this[type], '!all') < 0) return this[type].length;
  return 'All';
};

Mailpile.bulk_cache_add = function(type, value) {
  if (_.indexOf(this[type], value) < 0) {
    this[type].push(value);
  }
};

Mailpile.bulk_cache_remove = function(type, value) {
  if (_.indexOf(this[type], value) > -1) {
    this[type] = _.without(this[type], value);
  }
  // Removing anything at all implies we not everything is selected
  if (_.indexOf(this[type], '!all') > -1) {
    this[type] = _.without(this[type], '!all');
  }
};


