});/* This is the shared selection code (checkboxes etc).
** 
** Instead of a global object tracking which objects are selected, selection
** counts are calculated on-the-fly by evaluating the DOM.
**
** Selection contexts are grouped using the class "selection-context"; any
** checkboxes contained within the same selection context are assumed to be
** of the same type.
**
** The rationale for doing this, is it allows a single screen to have
** multiple groups of selectable items, each of which can be manipulated
** independently of the others. This should makes selection behavior stable,
** no matter how updates happen (AJAX, js, ...).
*/
Mailpile.UI.Selection = (function(){

var update_callbacks = {};

/* Helpers... */
function _call_callbacks(context, selected) {
  for (var i in update_callbacks) {
    update_callbacks[i](context, selected);
  }
}
function context(selector) {
  return $(selector).eq(0).closest('.selection-context').eq(0);
}
function _select_all(ctx) {
  return ctx.find('#pile-select-all-action');
}
function _each_checkbox(ctx, func) {
  ctx.find('input[type=checkbox]').each(func);
}


/**
 * Register a callback function, invoked when selection state changes
 * @param {String} name - Callback name
 * @param {Function} callback - Callback function
 */
function register(name, callback) {
  update_callbacks[name] = callback;
}

/**
 * Unregister a callback function
 * @param {String} name - Callback name
 */
function unregister(name) {
  delete update_callbacks[name];
}

/**
 * Select "these", all visible elements on page
 * @param {String|Object} selector - A JQuery selector or DOM element
 * @param {Boolean} [no_callbacks] - Skip invoking callbacks
 * @return {Array} Array of selected values
 */
function select_these(selector, no_callbacks) {
  var ctx = context(selector);
  var sel = [];
  _each_checkbox(ctx, function() {
    if (this.value == '!all') this.value = '';
    if (this.value != '') sel.push(this.value);
    $(this).prop('checked', true);
  });
  if (!no_callbacks) _call_callbacks(ctx, sel);
  return sel;
}

/**
 * Select nothing
 * @param {String|Object} selector - A JQuery selector or DOM element
 * @param {Boolean} [no_callbacks] - Skip invoking callbacks
 * @return {Array} Array of selected values, should be []
 */
function select_none(selector, no_callbacks) {
  var ctx = context(selector);
  _each_checkbox(ctx, function() {
    if (this.value == '!all') this.value = '';
    $(this).prop('checked', false);
  });
  if (!no_callbacks) _call_callbacks(ctx, []);
  return [];
}

/**
 * Select "all", potentially including things not visible on page
 * @param {String|Object} selector - A JQuery selector or DOM element
 * @param {Boolean} [no_callbacks] - Skip invoking callbacks
 * @return {Array} Array of selected values, should be ['!all']
 */
function select_all(selector, no_callbacks) {
  var ctx = context(selector);
  Mailpile.UI.Selection.select_these(ctx, true);
  _select_all(ctx).val('!all');
  if (!no_callbacks) _call_callbacks(ctx, ['!all']);
  return ['!all'];
}

/**
 * Stop selecting "all"
 * @param {String|Object} selector - A JQuery selector or DOM element
 * @param {Boolean} [no_callbacks] - Skip invoking callbacks
 * @return {Array} Array of selected values
 */
function select_not_all(selector, no_callbacks) {
  var ctx = context(selector);
  _select_all(ctx).val('');
  var sel = Mailpile.UI.Selection.selected(ctx);
  if (!no_callbacks) _call_callbacks(ctx, sel);
  return sel;
}

/**
 * Returns a list of currently selected items.
 * @param {String|Object} selector - A JQuery selector or DOM element
 * @return {Array} Array of selected values or ['!all']
 */
function selected(selector) {
  var ctx = context(selector);
  var selected = [];
  var all = false;
  _each_checkbox(ctx, function() {
    var $elem = $(this);
    if ($elem.is(':checked') && this.value) {
      if (this.value == '!all') all = true;
      selected.push(this.value);
    }
  });
  if (all) return ['!all'];
  return selected;
}

/**
 * Returns the number of selected items or a translation of "all".
 * @param {Array} Array of selected values or ['!all']
 * @return {String} Length of selection as a string
 */
function human_length(selection) {
  if (selection && selection[0] != '!all') return ('' + selection.length);
  return 'All';
}

return {
  'register': register,
  'unregister': unregister,
  'context': context,
  'select_these': select_these,
  'select_none': select_none,
  'select_all': select_all,
  // From the Zoolander school for kids who want to learn
  // to code good and do other stuff good too!
  'select_not_all': select_not_all,
  'selected': selected,
  'human_length': human_length
}})();/* This is shared message tagging/untagging code.
*/
Mailpile.UI.Tagging = (function(){

var operations = {
  'tag':     ['Tagged 1 message',
              'Tagged (num) messages'],
  'untag':   ['Untagged 1 message',
              'Untagged (num) messages'],
  'read':    ['Marked 1 message read',
              'Marked (num) messages read'],
  'unread':  ['Marked 1 message unread',
              'Marked (num) messages unread'],
  'move':    ['Moved 1 message',
              'Moved (num) messages'],
  'archive': ['Archived 1 message',
              'Archived (num) messages'],
  'trash':   ['Moved 1 message to trash',
              'Moved (num)  messages to trash'],
  'spam':    ['Moved 1 message to spam',
              'Moved (num) messages to spam']
};

/**
 * Tag/untag messages and then update the visible UI to match
 * @param {String|Object} selector - A JQuery selector or DOM element
 * @param {Boolean} [no_callbacks] - Skip invoking callbacks
 * @return {Array} Array of selected values
 */
function tag_and_update_ui(options, op, callback) {
  // If there's nothing to do, don't bother the back-end.
  if (!options.mid) return;
  if (!options.add && !options.del) return;

  var notify_done = Mailpile.notify_working("Tagging...", 500);
  options._error_callback = notify_done;

  Mailpile.API.tag_post(options, function(response) {
    notify_done();

    if ((!response.result) || (response.status != "success")) {
      // Just report errors, do nothing else.
      Mailpile.notification(response);
      return;
    }

    var count = response.result.msg_ids.length;
    if (count < 1) return; // This was a no-op.

    // Call callbacks, if any. We do this first, because callbacks may
    // want to reference bits of the DOM that get changed below.
    if (callback) callback(response);

    $('.pile-results').each(function (i, context) {
      var $context = $(context);

      var tids = (($context.data("tids") || "") + "").split(/\s+/);
      var context_tids = {};
      for (var i in tids) { context_tids[tids[i]] = true; }

      for (var i in response.result.untagged) {
        var tag = response.result.untagged[i][0];
        var mids = response.result.untagged[i][1];
        var untagged = {};
        for (var j in mids) { untagged[mids[j]] = true; }

        $context.find('.pile-message').each(function(i, elem) {
          var $elem = $(elem);
          var mid = $elem.data('mid');
          if (untagged[mid]) {
            if (context_tids[tag.tid]) {
              // Message should no longer appear in this context at all
              $elem.slideUp(200, function() { $(this).remove(); });
            }
            else {
              // Remove any tag labels
              $elem.removeClass('in_' + tag.slug)
                   .find('.pile-message-tag-' + tag.tid).remove();
            }
          }
        });
      }

      // Add tag labels to elements as necessary
      for (var i in response.result.tagged) {
        var tag = response.result.tagged[i][0];
        if (!context_tids[tag.tid]) {
          var mids = response.result.tagged[i][1];
          var tagged = {};
          for (var j in mids) { tagged[mids[j]] = true; }

          var hex = Mailpile.theme.colors[tag.label_color];
          var tag_html = (
            '<span class="pile-message-tag pile-message-tag-' + 
                          tag.tid + '" style="color: ' + hex + ';">' +
                          '<span class="pile-message-tag-icon ' + tag.icon +
                          '"></span></span>'
          );
          $context.find('.pile-message').each(function(i, elem) {
            var $elem = $(elem);
            var mid = $elem.data('mid');
            if (tagged[mid]) {
              if (tag.flag_hides) {
                // If tag is a hiding tag, make element disappear
                $elem.slideUp(200, function() { $(this).remove(); });
              }
              else {
                $elem.addClass('in_' + tag.slug);
                if (tag.label) {
                  $elem.find('span.item-tags').append(tag_html);
                }
              }
            }
          });
        }
      }

    });

    // Update Bulk UI; do this after a delay, so the fade-outs can complete.
    setTimeout(Mailpile.bulk_actions_update_ui, 300);

    // Override message text if we think we know better
    if (op && operations[op]) {
      if (count == 1) {
        response.message = operations[op][0];
      }
      else {
        var msg = operations[op][1];
        var ofs = msg.indexOf('(num)');
        response.message = (msg.substring(0, ofs) +
                            count +
                            msg.substring(ofs+5));

      }
    }

    // Show notification
    Mailpile.notification(response);
  });
}

