Mailpile.Composer.init = function(mid, strings, addresses) {

  // Reset tabindex for To: field
  $('#search-query').attr('tabindex', '-1');

  // Save Text Composing Objects (move to data model)
  //
  // FIXME: This is a bad pattern; here we're about to duplicate in JS
  //        land information that comes from the DOM. Duplication is bad.
  //
  Mailpile.Composer.Drafts[mid] = Mailpile.Composer.Model(strings, addresses);

  // Load Crypto States
  // FIXME: needs dynamic support for multi composers on a page
  Mailpile.Composer.Crypto.LoadStates(mid);

  // Initialize select2
  Mailpile.Composer.Recipients.AddressField('compose-to-' + mid);

  if ($('#compose-cc-' + mid).val()) {
    Mailpile.Composer.Recipients.AddressField('compose-cc-' + mid);
  }

  if ($('#compose-bcc-' + mid).val()) {
    Mailpile.Composer.Recipients.AddressField('compose-bcc-' + mid);
  }

  // Show Crypto Tooltips
  Mailpile.Composer.Crypto.UpdateEncryptionState(mid, function() {
    Mailpile.Composer.Tooltips.Signature();
    Mailpile.Composer.Tooltips.Encryption();
    Mailpile.Composer.Tooltips.ContactDetails();
  });

  // Initialize Attachments; use setTimeout to isolate faults.
  setTimeout(function() {
    // FIXME: needs to be bound to unique ID that can be destroyed
    Mailpile.Composer.Attachments.Uploader({
      browse_button: 'compose-attachment-pick-' + mid,
      container: 'compose-attachments-' + mid,
      mid: mid
    });
  }, 100);

  // Body
  Mailpile.Composer.Body.Setup(mid);
};/* Composer - Crypto */

Mailpile.Composer.Crypto.UpdateEncryptionState = function(mid, chain) {
  // Assemble all the recipient addresses, as well as our sending address
  var emails = [$('#compose-from-selected-' + mid).find('.address').html()];
  Mailpile.Composer.Recipients.GetAll(mid, function(rcpt) {
    emails.push(rcpt.address);
  });
  // Ask the back-end for updated address-book info and an aggregate
  // recommended crypto policy.
  Mailpile.API.crypto_policy_get({email: emails}, function(response) {
    var r = response.result;

    // Update attach key (or not) state
    if (r['can-sign']) {
      $('.compose-attach-key').show();
    }
    else {
      $('.compose-attach-key').hide();
    }
    $('#compose-attach-key-' + mid).prop('checked',
                                         r['can-sign'] && r['send-keys']);
    Mailpile.Composer.Crypto.AttachKey(mid);

    // Record our capabilities: can we encrypt? Sign?
    $('#compose-crypto-encryption-' + mid).data('can', r['can-encrypt']);
    $('#compose-crypto-signature-' + mid).data('can', r['can-sign']);
    if (emails.length > 1) {
      // Update encrypt/sign icons
      Mailpile.Composer.Crypto.LoadStates(mid, r['crypto-policy'],
                                               r['reason']);

      // Embellish our recipients with data from the backend; in particular
      // this adds the keys and avatars to things manually typed.
      Mailpile.Composer.Recipients.WithToCcBcc(mid, function(field, rcpts) {
        var changed = false;
        for (var i in rcpts) {
          var updated = r.addresses[rcpts[i].address];
          if (updated) {
            if (rcpts[i].fn.indexOf(' ') < 0) rcpts[i].fn = updated.fn;
            rcpts[i].keys = updated.keys;
            rcpts[i].flags = updated.flags;
            rcpts[i].photo = updated.photo;
            changed = true;
          }
        };
        if (changed) Mailpile.Composer.Recipients.Update(mid, field, rcpts);
      });
    }
    else {
      Mailpile.Composer.Crypto.LoadStates(mid, 'none', '');
    }
    if (chain) chain(mid);
  });
};


Mailpile.Composer.Crypto.Unencryptables = function(mid) {
  var unencryptable = [];
  Mailpile.Composer.Recipients.GetAll(mid, function(rcpt) {
    if (!rcpt.flags.secure) unencryptable.push(rcpt);
  });
  return unencryptable;
};


Mailpile.Composer.Crypto.LoadStates = function(mid, state, reason) {
  state = state || $('#compose-crypto-' + mid).val();

  var signature = 'none';
  if (state.match(/sign/)) {
    signature = 'sign';
  }
  Mailpile.Composer.Crypto.SignatureToggle(signature, mid);

  var encryption = 'none';
  if (state.match(/encrypt/)) {
    encryption = 'encrypt';
  }
  Mailpile.Composer.Crypto.EncryptionToggle(encryption, mid);

  // FIXME: We need to know if encryption or signing is REQUIRED, and
  // disable or enable the send button based on that. The conflict state
  // doesn't cover for when the user does illegal things manually.
  $('#form-compose-' + mid + ' button[name=send]')
    .data('crypto-state', state || '')
    .data('crypto-reason', reason || '')
    .attr('title', reason || '')
    .css({ 'opacity': (state.match(/conflict/)) ? 0.25 : 1.0 });
};


/* Compose - Set crypto state of message */
Mailpile.Composer.Crypto.SetState = function(mid) {

  // Returns: none, openpgp-sign, openpgp-encrypt and openpgp-sign-encrypt
  var state = 'none';
  var signature = $('#compose-signature-' + mid).val();
  var encryption = $('#compose-encryption-' + mid).val();

  if (signature == 'sign' && encryption == 'encrypt') {
    state = 'openpgp-sign-encrypt'; 
  }
  else if (signature == 'sign') {
    state = 'openpgp-sign';
  }
  else if (encryption == 'encrypt') {
    state = 'openpgp-encrypt';
  }
  else {
    state = 'none';
  }

  $('#compose-crypto-' + mid).val(state);
  return state;
};


/* Compose - Render crypto "signature" of a message */
Mailpile.Composer.Crypto.SignatureToggle = function(status, mid, manual) {
  if ($('#compose-crypto-signature-' + mid).data('can') === false) {
    status = (status == 'cannot') ? status : 'none';
  }
  if (status === 'sign') {
    $('#compose-crypto-signature-' + mid).data('crypto_color', 'crypto-color-green');
    $('#compose-crypto-signature-' + mid).attr('title', 'This message will be verifiable to recipients who have your encryption key. They will know it actually came from you :)');
    $('#compose-crypto-signature-' + mid).find('span.icon').removeClass('icon-signature-none').addClass('icon-signature-verified');
    $('#compose-crypto-signature-' + mid).find('span.text').html('Signed');
    $('#compose-crypto-signature-' + mid).removeClass('none').addClass('signed bounce');

  } else if (status === 'none') {
    $('#compose-crypto-signature-' + mid).data('crypto_color', 'crypto-color-gray');
    $('#compose-crypto-signature-' + mid).attr('title', 'This message will not be verifiable, recipients will have no way of knowing it actually came from you.');
    $('#compose-crypto-signature-' + mid).find('span.icon').removeClass('icon-signature-verified').addClass('icon-signature-none');
    $('#compose-crypto-signature-' + mid).find('span.text').html('Unsigned');
    $('#compose-crypto-signature-' + mid).removeClass('signed').addClass('none bounce');

  } else {
    $('#compose-crypto-signature-' + mid).data('crypto_color', 'crypto-color-red');
    $('#compose-crypto-signature-' + mid).attr('title', 'Verification Error');
    $('#compose-crypto-signature-' + mid).find('span.icon').removeClass('icon-signature-none icon-signature-verified').addClass('icon-signature-error');
    $('#compose-crypto-signature-' + mid).find('span.text').html('Error accessing your encryption key');
    $('#compose-crypto-signature-' + mid).removeClass('none').addClass('error bounce');
  }

    // Set Form Value
  if ($('#compose-signature-' + mid).val() !== status) {
    $('#compose-crypto-signature-' + mid).addClass('bounce');
    $('#compose-signature-' + mid).val(status);

    // Remove Animation
    setTimeout(function() {
      $('#compose-crypto-signature-' + mid).removeClass('bounce');
    }, 1000);

    Mailpile.Composer.Crypto.SetState(mid);
  }
};


/* Compose - Render crypto "encryption" of a message */
Mailpile.Composer.Crypto.EncryptionToggle = function(status, mid, manual) {
  var encryption = $('#compose-encryption-' + mid).val();
  var can = $('#compose-crypto-encryption-' + mid).data('can');
  if (!manual && (status === 'none') && (encryption === 'encrypt') && !can) {
    // If we were encrypting, but as a side-effect are no longer capable of
    // doing so, then we go to the "cannot" state instead of "none".
    status = 'cannot';
  }
  else if (!can) {
    status = (status == 'cannot') ? status : 'none';
  }

  if (status === 'encrypt') {
    $('#compose-crypto-encryption-' + mid).data('crypto_color', 'crypto-color-green');
    $('#compose-crypto-encryption-' + mid).attr('title', 'This message and attachments will be encrypted. The recipients &amp; subject (metadata) will not');
    $('#compose-crypto-encryption-' + mid).find('span.icon').removeClass('icon-lock-open').addClass('icon-lock-closed');
    $('#compose-crypto-encryption-' + mid).find('span.text').html('Encrypted');
    $('#compose-crypto-encryption-' + mid).removeClass('none error cannot').addClass('encrypted');

  } else if (status === 'cannot') {
    $('#compose-crypto-encryption-' + mid).data('crypto_color', 'crypto-color-orange');
    $('#compose-crypto-encryption-' + mid).attr('title', 'This message cannot be encrypted because you do not have keys for one or more recipients');
    $('#compose-crypto-encryption-' + mid).find('span.icon').removeClass('icon-lock-closed').addClass('icon-lock-open');
    $('#compose-crypto-encryption-' + mid).find('span.text').html('Can Not Encrypt');
    $('#compose-crypto-encryption-' + mid).removeClass('none encrypted error').addClass('cannot');

  } else if (status === 'none' || status == '') {
    $('#compose-crypto-encryption-' + mid).data('crypto_color', 'crypto-color-gray');
    $('#compose-crypto-encryption-' + mid).attr('title', 'This message and metadata will not be encrypted');
    $('#compose-crypto-encryption-' + mid).find('span.icon').removeClass('icon-lock-closed').addClass('icon-lock-open');
    $('#compose-crypto-encryption-' + mid).find('span.text').html('None');
    $('#compose-crypto-encryption-' + mid).removeClass('encrypted cannot error').addClass('none');

  } else {
    $('#compose-crypto-encryption-' + mid).data('crypto_color', 'crypto-color-red');
    $('#compose-crypto-encryption-' + mid).attr('title', 'There was an error prepping this message for encryption');
    $('#compose-crypto-encryption-' + mid).find('span.icon').removeClass('icon-lock-open icon-lock-closed').addClass('icon-lock-error');
    $('#compose-crypto-encryption-' + mid).find('span.text').html('Error Encrypting');
    $('#compose-crypto-encryption-' + mid).removeClass('encrypted cannot none').addClass('error');
  }

  // Set Form Value
  if ($('#compose-encryption-' + mid).val() !== status) {
    $('#compose-crypto-encryption-' + mid).addClass('bounce');
    $('#compose-encryption-' + mid).val(status);

    // Remove Animation
    setTimeout(function() {
      $('#compose-crypto-encryption-' + mid).removeClass('bounce');
    }, 1000);

    Mailpile.Composer.Crypto.SetState(mid);
  }
};


Mailpile.Composer.Crypto.AttachKey = function(mid) {
  var checkbox = $('#compose-attach-key-' + mid);
  var hiddenak = $('#compose-hidden-attach-key-' + mid);
  if (checkbox.is(':checked')) {
    hiddenak.val('yes');
  } else {
    hiddenak.val('no');
  }
};/* Compose - Autosave */

Mailpile.Composer.Autosave = function(mid, form_data, callback) {

  if (Mailpile.Composer.Drafts[mid] === undefined) {
    Mailpile.Composer.Drafts[mid] = Mailpile.Composer.Model({}, {});
    Mailpile.Composer.Body.Setup(mid);
  }

  // Has text changed, or is new?  If so, run autosave.
  if ($('#compose-text-' + mid).val() != Mailpile.Composer.Drafts[mid].body) {

    // UI Feedback
    var autosave_msg = $('#compose-message-autosaving-' + mid).data('autosave_msg');
    $('#compose-message-autosaving-' + mid).html(autosave_msg).fadeIn();

    // Autosave It
  	$.ajax({
  		url			 : Mailpile.api.compose_save,
  		type		 : 'POST',
      timeout  : 15000,
  		data     : form_data,
  		dataType : 'json',
  	  success  : function(response) {

        // Update Message (data model)
        Mailpile.Composer.Drafts[mid].body = $('#compose-text-' + mid).val();

        // Fadeout autosave UI msg
        setTimeout(function() {
          $('#compose-message-autosaving-' + mid).fadeOut();
        }, 2000);
        callback();
      },
      error: function() {
        var autosave_error_msg = $('#compose-message-autosaving-' + mid).data('autosave_error_msg');
        $('#compose-message-autosaving-' + mid).html('<span class="icon-x"></span>' + autosave_error_msg).fadeIn();
        callback();
      }
  	});

  }
  // Not Autosaving
  else {
    callback();
  }
};


Mailpile.Composer.AutosaveAll = function(delay, callback) {
  var save_chain = [];
  $('.form-compose').each(function(key, form) {
    save_chain.push(function(chain) {
      Mailpile.Composer.Autosave($(form).data('mid'), $(form).serialize(),
                                 function() {
        if (chain && chain.length) {
          var nxt = chain.shift();
          if (delay) {
            setTimeout(function() { nxt(chain); }, delay)
          }
          else {
            nxt(chain);
          }
        }
      });
    });
  });
  if (callback) save_chain.push(callback);
  if (save_chain.length) save_chain.shift()(save_chain);
}


/* Compose Autosave - finds each compose form and performs action */
Mailpile.Composer.AutosaveTimer = $.timer(function() {
  Mailpile.Composer.AutosaveAll(250);
});/* Compose - Attachments */

Mailpile.Composer.Attachments.UploaderImagePreview = function(attachment, file) {

  // Create an instance of the mOxie Image object. This
  // utility object provides several means of reading in
  // and loading image data from various sources.
  // Wiki: https://github.com/moxiecode/moxie/wiki/Image
  var preloader = new mOxie.Image();

  // Define the onload BEFORE you execute the load()
  // command as load() does not execute async.
  preloader.onload = function() {

    // Scale the image (in memory) before rendering it
    preloader.downsize(150, 150);

    // Grab preloaded the Base64 encoded image data
    attachment['attachment_data'] = preloader.getAsDataURL();

    var attachment_image_template = Mailpile.safe_template($('#template-composer-attachment-image').html());
    var attachment_image_html = attachment_image_template(attachment);

    // Append template to view
    $('#compose-attachments-files-' + attachment.mid).append(attachment_image_html);
  };

  // Calling the .getSource() returns instance of mOxie.File
  // Wiki: https://github.com/moxiecode/plupload/wiki/File
  preloader.load(file.getSource());
};


Mailpile.Composer.Attachments.ExistingImagePreview = function(attachment, file) {

  // Load static preview
  attachment['attachment_data'] = '/message/download/preview/=' + attachment.mid + '/' + attachment.aid + '/';

  var attachment_image_template = Mailpile.safe_template($('#template-composer-attachment-image').html());
  var attachment_image_html = attachment_image_template(attachment);

  // Append template to view
  $('#compose-attachments-files-' + attachment.mid).append(attachment_image_html);
};


Mailpile.Composer.Attachments.UpdatePreviews = function(attachments, mid, file) {

  // Loop through attachments
  _.each(attachments, function(attachment, key) {

    if (!$('#compose-attachment-' + mid + '-' + attachment.aid).length) {

      attachment['previewable'] = _.indexOf(['image/bmp', 
                                      'image/gif',
                                      'image/jpg',
                                      'image/jpeg',
                                      'image/pjpeg',
                                      'image/x-png',
                                      'image/png',
                                      'application/vnd.google-apps.photo'], attachment.mimetype);

      if (file && file.name === attachment.filename) {
        attachment['is_file'] = true;
      } else {
        attachment['is_file'] = false;
      }

      // More UI friendly values
      var file_parts = attachment.filename.split('.');
      var file_parts_length = file_parts.length

      if (file_parts.length > 2 || attachment.filename.length > 20) {
        attachment['name_fixed'] = attachment.filename.substring(0, 16);
      } else {
        attachment['name_fixed'] = file_parts[0];
      }

      attachment['mid'] = mid;
      attachment['size'] = plupload.formatSize(attachment.length);
      attachment['extension'] = file_parts[file_parts.length - 1];

      // Determine Preview Type (live image, req image, graphic)
      if (attachment.previewable > -1 && attachment.is_file) {
        Mailpile.Composer.Attachments.UploaderImagePreview(attachment, file);
      }
      else if (attachment.previewable > -1 && !attachment.is_file) {
        Mailpile.Composer.Attachments.ExistingImagePreview(attachment);
      }
      else {
        var attachment_template = Mailpile.safe_template($('#template-composer-attachment').html());
        var attachment_html = attachment_template(attachment);
      	$('#compose-attachments-files-' + mid).append(attachment_html);
      }
    } else {
      console.log('attachment exists ' + attachment.aid);
    }
  });
};


Mailpile.Composer.Attachments.Uploader = function(settings) {

  var uploader = new plupload.Uploader({
  	runtimes : 'html5',
  	browse_button : settings.browse_button, // you can pass in id...
  	container: settings.container, // ... or DOM Element itself
    drop_element: settings.container,
  	url : Mailpile.API.U('/api/0/message/attach/'),
    multipart : true,
    multipart_params : {
      'mid': settings.mid,
      'csrf': Mailpile.csrf_token
    },
    file_data_name : 'file-data',
  	filters : {
  		max_file_size : '50mb'
  	},
    resize: {
      width: '3600',
      height: '3600',
      crop: true,
      quaility: 100
    },
    views: {
      list: true,
      thumbs: true,
      active: 'thumbs'
    },
  	init: {
      PostInit: function() {
        $('#compose-attachments-' + settings.mid).find('.compose-attachment-pick').removeClass('hide');
        $('#compose-attachments-' + settings.mid).find('.attachment-browswer-unsupported').addClass('hide');
        uploader.refresh();
      },
      FilesAdded: function(up, files) {

        // Loop through added files
      	plupload.each(files, function(file) {

          // Show Warning for 50 mb or larger
          if (file.size > 52428800) {
            start_upload = false;
            alert(file.name + ' is ' + plupload.formatSize(file.size) + '. Some people cannot receive attachments larger than 50 Megabytes.');
          } else {

            // Start
            uploader.start();
          }
      	});
      },
      UploadProgress: function(up, file) {
      	$('#' + file.id).find('b').html('<span>' + file.percent + '%</span>');
      },
      FileUploaded: function(up, file, response) {

        if (response.status == 200) {

          var response_json = $.parseJSON(response.response);
          var new_mid = response_json.result.message_ids[0];

          //console.log(file);
          Mailpile.Composer.Attachments.UpdatePreviews(response_json.result.data.messages[new_mid].attachments, settings.mid, file);

        } else {
          Mailpile.notification({status: 'error', message: 'Attachment upload failed status: ' + response.status });
        }
      },
      Error: function(up, err) {
        Mailpile.notification({status: 'error', message: 'Could not upload attachment because: ' + err.message });
        $('#' + err.file.id).find('b').html('Failed ' + err.code);
        uploader.refresh();
      }
    }
  });

  return uploader.init();
};


Mailpile.Composer.Attachments.Remove = function(mid, aid) {
  Mailpile.API.message_unattach_post({ mid: mid, att: aid }, function(result) {
    if (result.status == 'success') {
      $('#compose-attachment-' + mid + '-' + aid).fadeOut(function() {
        $(this).remove();
        Mailpile.Composer.Attachments.UpdatePreviews(result.result.data.messages[mid].attachments, mid, false);
      });
    } else {
      Mailpile.notification(result);
    }
  });
};/* Composer - Recipients */

Mailpile.Composer.Recipients.Get = function(mid, which) {
  var $elem = $('#compose-' + which + '-' + mid);
  return Mailpile.Composer.Recipients.Analyze($elem.val());
};


Mailpile.Composer.Recipients.WithToCcBcc = function(mid, callback) {
  var fields = ['to', 'cc', 'bcc'];
  for (var i in fields) {
    var rcpts = Mailpile.Composer.Recipients.Get(mid, fields[i]);
    callback(fields[i], rcpts);
  }
};


Mailpile.Composer.Recipients.GetAll = function(mid, filter) {
  var recipients = [];
  Mailpile.Composer.Recipients.WithToCcBcc(mid, function(field, rcpts) {
    for (var j in rcpts) {
      recipients.push(filter ? filter(rcpts[j]) : rcpts[j]);
    }
  });
  return recipients;
};


Mailpile.Composer.Recipients.AnalyzeAddress = function(address, preset) {
  /* The Mailpile API guarantees consistent formatting of addresses, so
     a simple regexp should generally work juuuust fine. However, we also
     want to handle pasted or typed input. So we try the simple parse first,
     and then get a bit more creative. */
  var check = address.match(/^\s*([^<]*?)\s*<?([^\s<>]+@[^\s<>#]+)(#[a-zA-Z0-9]+)?>?\s*$/);
  if (!check) {
    var check2 = address.match(/^\s*([^\s]+)(@[^\s]+)\s*$/);
    if (check2) {
      check = [check2[0], check2[1], check2[1] + check2[2], false];
    }
    else {
      check = [address, address, address, false];
    }
  }
  var fn = $.trim(check[1] || check[2].substring(0, check[2].indexOf('@')));
  if (fn.substring(0, 1) == '<') fn = fn.substring(1);
  var parsed = {
    "id": check[2],
    "fn": fn,
    "address": check[2],
    "keys": [],
    "flags": {"secure" : false, "manual": !preset}
  };
  if (check[3]) {
    parsed["keys"] = [{"fingerprint": check[3].substring(1)}];
    parsed["flags"] = {"secure" : true};
  };
  return parsed;
};


/* Composer - tokenize input field (to: cc: bcc:) */
Mailpile.Composer.Recipients.Analyze = function(addresses) {
  var existing = [];

  // Is Valid & Has Multiple
  if (addresses) {
    // We know this simple strategy works, because the backend formats the
    // address lines in a conisistent way.
    var multiple = addresses.split(/>, */);

    $.each(multiple, function(key, value) {
      if (value.indexOf('@') > -1) {
        if (value.indexOf('<') == -1) value = value + ' <' + value;
        if (value.indexOf('>') == -1) value = value + '>';
      }
      existing.push(Mailpile.Composer.Recipients.AnalyzeAddress(value, true));
    });
  }
  return existing;
};


Mailpile.Composer.Recipients.RecipientToAddress = function(object) {
  if (object.flags.secure) {
    address = object.address + '#' + object.keys[0].fingerprint;
  } else {
    address = object.address;
  }
  if (object.fn !== "" && object.address !== object.fn) {
    return object.fn + ' <' + address + '>';
  } else {
    return '<' + address + '>';
  }
};


/* Composer - instance of select2 */
Mailpile.Composer.Recipients.AddressField = function(id) {

  // Get MID
  var mid = $('#' + id).data('mid');

  $('#' + id).select2({
    id: Mailpile.Composer.Recipients.RecipientToAddress,
    ajax: {
      // instead of writing the function to execute the request we use
      // Select2's convenient helper
      url: Mailpile.api.contacts,
      quietMillis: 1,
      cache: true,
      dataType: 'json',
      data: function(term, page) {
        return {
          q: term
        };
      },
      results: function(response, page) {
        // Convert the results into the format expected by Select2
        return {
          results: response.result.addresses
        };
      }
    },
    multiple: true,
    allowClear: true,
    width: '100%',
    minimumInputLength: 1,
    minimumResultsForSearch: -1,
    placeholder: 'Type to add contacts',
    maximumSelectionSize: 100,
    tokenSeparators: [", ", ";"],
    createSearchChoice: Mailpile.Composer.Recipients.AnalyzeAddress,
    formatResult: function(state) {
      var avatar = '<span class="icon-user"></span>';
      var secure = '';
      if (state.photo) {
        avatar = '<img src="' + _.escape(state.photo) + '">';
      }
      if (state.flags.secure) {
        secure = '<span class="icon-lock-closed"></span>';
      }
      return ('<span class="compose-select-avatar">' + avatar + '</span>' +
              '<span class="compose-select-name">' + 
              _.escape(state.fn) + secure + '<br>' +
              '<span class="compose-select-address">' + state.address +
              '</span></span>');
    },
    formatSelection: function(state, elem) {
      // Update Model
      var found = false;
      var model = Mailpile.Composer.Drafts[mid];
      for (i in model.addresses) {
        var contact_data = model.addresses[i];
        if (contact_data.address == state.address) {
          if (state.flags.manual) {
            state.photo = contact_data.photo;
            model.addresses[i] = state;
          }
          else {
            state = model.addresses[i];
          }
          found = i;
        }
      }
      if (!found) {
        found = Math.random().toString(16).substring(6);
        model.addresses[found] = state;
      }

      // Create HTML
      var avatar = '<span class="avatar icon-user" data-address="' + _.escape(state.address) + '"></span>';
      var name   = state.fn;
      var secure = '';

      if (state.photo) {
        avatar = '<span class="avatar"><img src="' + _.escape(state.photo) + '" data-address="' + state.address + '"></span>';
      }
      if (!state.fn) {
        name = state.address;
      }
      if (state.flags.secure) {
        secure = '<span class="icon-lock-closed" data-address="' + _.escape(state.address) + '"></span>';
      }

